mapper.update:
UPDATE REVIEWS SET REVIEW_TITLE = #{reviewTitle}, REVIEW_CONTENT = #{reviewContent}, FUNERAL_DATE = #{funeralDate}, LOCATION = #{location}, UPDATED_AT = NOW()
, FILE_NAME = #{fileName}
, ORIGINAL_FILE_NAME = #{originalFileName}
WHERE ID = #{id} LIMIT 1

mapper.delete:

DELETE FROM REVIEWS
WHERE ID = #{id}
LIMIT 1
